
  
  $(function () {

    function handlesidebar() {
      $(".sidebartoggler").on("click", function () {
        $(".sidebartoggler").prop("checked", !0);
        $("#main-wrapper").toggleClass("show-left-sidebar");

      });
    }
    handlesidebar();

    function handlerightsidebar() {
      $(".right-sidebartoggler").on("click", function () {
        $(".right-sidebartoggler").prop("checked", !0);
        $("#main-wrapper").toggleClass("show-right-sidebar");

      });
    }
    handlerightsidebar();

    function close_sidebar() {
        $(".backdrops").on("click", function () {
            $(".backdrops").prop("checked", !0);
            $("#main-wrapper").removeClass("show-left-sidebar");
            $("#main-wrapper").removeClass("show-right-sidebar");
    
          });
    }
    
   close_sidebar();

  });
  